contents:
/* Modal Header */
my-modal-component {
  font-family: -apple-system, BlinkMacSystemFont, 'Segoe UI', Roboto,
    'Helvetica Neue', Arial, 'Noto Sans', 'Liberation Sans', sans-serif,
    'Apple Color Emoji', 'Segoe UI Emoji', 'Segoe UI Symbol', 'Noto Color Emoji';
}

my-modal-component > .modal-header {
  display: flex;
  flex-direction: row;
  justify-content: space-between;
  align-items: center;
  background-color: #fff;
  border: solid 1px #ccc;
  border-bottom: none;
  padding: 10px;
}

my-modal-component > .modal-header > .modal-title {
  font-weight: 700;
}

my-modal-component > .modal-header > .modal-close {
  cursor: pointer;
  padding: 5px 10px;
}

/* Modal Body */
my-modal-component > .modal-body {
  background-color: #fff;
  border: solid 1px #ccc;
  min-height: 200px;
  min-width: 300px;
  padding: 10px;
}

/* Modal Footer */
my-modal-component > .modal-footer {
  display: flex;
  background-color: #fff;
  border: solid 1px #ccc;
  border-top: none;
  justify-content: flex-end;
  padding: 10px;
}

my-modal-component .btn-cancel {
  margin-right: 0.5rem;
}
